import base64
import json

F_STATE_TEMPLATE = ''


def _decode_base64(data):
    missing_padding = 4 - len(data) % 4
    if missing_padding:
        data += '=' * missing_padding
    return json.loads(str(base64.b64decode(data), encoding='utf-8'))


def _encode_base64(data):
    data = json.dumps(data, ensure_ascii=False, separators=(',', ':'))
    bytes_data = data.encode("utf-8")
    return (base64.b64encode(bytes_data)).decode()


def get_f_state(cur_date, t: str):
    data = _decode_base64(F_STATE_TEMPLATE)
    data.get('p1').update({'Title': ["每日两报（上午）", "每日两报（下午）"][int(t)-1]})
    data.get('p1_BaoSRQ').update({'Text': cur_date})
    data.get('p1_ZaiXiao').update({'SelectedValue': '宝山'})
    data.get('p1_ddlXian').update({'SelectedValueArray': ['宝山区']})

    return _encode_base64(data)


